mapper.update:
UPDATE address_book
consignee = #{consignee}, sex = #{sex}, phone = #{phone}, state_name = #{stateName}, city_name = #{cityName}, postcode = #{postcode}, detail = #{detail}, label = #{label}, is_default = #{isDefault}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT *
FROM address_book


        AND user_id = #{userId}


        AND phone = #{phone}


        AND is_Default = #{isDefault}
