mapper.update:
UPDATE dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
WHERE id = #{id}

UPDATE dish
name = #{name}, category_id = #{categoryId}, status = #{status},
WHERE id = #{id}

mapper.select:
select d.* , c.name as categoryName from dish d left outer join category c on d.category_id = c.id


        and d.name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by create_time desc

SELECT id, name, category_id AS categoryId, price, image, description, status, update_time AS updateTime FROM dish
AND category_id = #{categoryId}
AND status = #{status}
ORDER BY update_time DESC

SELECT COUNT(id)
FROM dish


        AND status = #{status}


        AND category_id = #{categoryId}
