mapper.update:
UPDATE orders
status = #{status}, cancel_reason = #{cancelReason}, cancel_time = #{cancelTime}, estimated_delivery_time = #{estimatedDeliveryTime},
WHERE id = #{id}

mapper.select:
SELECT SUM(amount) FROM orders
WHERE
1 = 1

    AND order_time >= #{begin}


    AND order_time <= #{end}


    AND status = #{status}

SELECT count(id) from orders


        and order_time > #{begin}


        and order_time < #{end}


        and status = #{status}

SELECT od.name,sum(od.number) number from order_detail od,orders o where od.order_id = o.id and o.status = 5
and o.order_time > #{begin}
and o.order_time < #{end}
group by od.name order by number desc limit 0,10

SELECT id, order_id, name, number, amount, image
FROM order_detail
WHERE order_id = #{orderId}

SELECT * FROM orders
WHERE 1=1

    AND number LIKE CONCAT('%', #{number}, '%')


    AND phone = #{phone}


    AND status = #{status}


    AND order_time >= #{startDate}


    AND order_time <= #{endDate}

ORDER BY order_time DESC
