mapper.update:
UPDATE setmeal
SET status = #{status}
WHERE id = #{id}

UPDATE setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, description = #{description}, image = #{image}, status = #{status}, update_time = NOW()
WHERE id = #{id}

mapper.select:
SELECT *
FROM setmeal


        AND name like concat('%',#{name},'%')


        AND category_id = #{categoryId}


        AND status = #{status}

SELECT s.*, c.name as categoryName FROM setmeal s LEFT JOIN category c ON s.category_id = c.id
AND s.name LIKE CONCAT('%', #{name}, '%')
AND s.category_id = #{categoryId}
AND s.status = #{status}
ORDER BY s.id DESC

SELECT COUNT(id)
FROM setmeal


        AND status = #{status}


        AND category_id = #{categoryId}
